require_relative "output_statements"




class Computer
  include OutputStatements

  attr_accessor :colors_beginner,
                :colors_intermediate,
                :colors_expert,
                :generated_colors,
                :correct_guesses,
                :response,
                :guess,
                :count,
                :start,
                :finish

  def initialize
    @colors_beginner = ["R", "Y", "B", "G"]
    @colors_intermediate = ["R", "Y", "B", "G", "W"]
    @colors_expert = ["R", "Y", "B", "G", "W", "O"]
    @generated_colors = []
    @response = []
    @guess = ""
    @count = 0
    @minute = ""
    @second = ""
  end

  def game_menu
    puts OutputStatements.level_selection
    @guess = gets.chomp.downcase
    if guess == "b" || guess == "beginner"
      game_flow_beginner
    elsif guess == "in" || guess == "intermediate"
      game_flow_intermediate
    else
      game_flow_expert
    end
  end

  def game_flow_beginner
    # binding.pry
    randomize_colors_beginner
    puts OutputStatements.start_game_beginner
    @guess = gets.chomp.downcase
    @start = Time.new
    until guess == generated_colors
      if guess == "c" || guess == "cheat"
        puts "#{generated_colors.join}"
        @guess = gets.chomp.downcase
      elsif guess == "q" || guess == "quit"
        quit_game
      elsif guess.length < 4
        puts "Too short try again."
        @guess = gets.chomp.downcase
      elsif guess.length > 4
        puts "Too long try again."
        @guess = gets.chomp.downcase
      else
        check_the_player_guess(guess)
        @guess = gets.chomp.downcase
      end
    end
  end

  def game_flow_intermediate
    randomize_colors_intermediate
    puts OutputStatements.start_game_intermediate
    @guess = gets.chomp.downcase
    @start = Time.new
    until guess == generated_colors
      if guess == "c" || guess == "cheat"
        puts "#{generated_colors.join}"
        @guess = gets.chomp.downcase
      elsif guess == "q" || guess == "quit"
        quit_game
      elsif guess.length < 6
        puts "Too short try again."
        @guess = gets.chomp.downcase
      elsif guess.length > 6
        puts "Too long try again."
        @guess = gets.chomp.downcase
      else
        check_the_player_guess(guess)
        @guess = gets.chomp.downcase
      end
    end
  end

  def game_flow_expert
    randomize_colors_expert
    puts OutputStatements.start_game_expert
    @guess = gets.chomp.downcase
    @start = Time.new
    until guess == generated_colors
      if guess == "c" || guess == "cheat"
        puts "#{generated_colors.join}"
        @guess = gets.chomp.downcase
      elsif guess == "q" || guess == "quit"
        quit_game
      elsif guess.length < 8
        puts "Too short try again."
        @guess = gets.chomp.downcase
      elsif guess.length > 8
        puts "Too long try again."
        @guess = gets.chomp.downcase
      else
        check_the_player_guess(guess)
        @guess = gets.chomp.downcase
      end
    end
  end

  def randomize_colors_beginner
    @generated_colors = 4.times.map{colors_beginner.sample(1)}.flatten
  end

  def randomize_colors_intermediate
    @generated_colors = 6.times.map{colors_intermediate.sample(1)}.flatten
  end

  def randomize_colors_expert
    @generated_colors = 8.times.map {colors_expert.sample(1)}.flatten
  end

  def check_the_player_guess(guess)
    convert_the_guess(guess)
    if @response == @generated_colors
      game_ended
    else
      puts "#{guess.upcase} has #{correct_color(response)} of the correct elements with #{exact_position(response)} colors  in the correct position"
      @count += 1
    end
  end

  def convert_the_guess(guess)
    @response = guess.upcase.split(//)
  end

  def exact_position(response)
    (@generated_colors.zip(@response).map{|x, y| x == y}).count(true)
  end

  def correct_color(response)
    (@response & @generated_colors).count
  end

