  def elapsed_time
    @finish = Time.new
    time = finish - @start
    Time.at(time).utc.strftime("%M:%S")
  end

  def game_ended

    puts "Congratulations! you have saved the world from Skynet! You
completed the game in #{@count} turn(s) with your guess #{@guess.upcase} and a time of #{elapsed_time} ! Would you like to (p)lay again or (q)uit?"
    @guess = gets.chomp.downcase
    if guess == "p" || guess == "play"
      game_menu
    else
      quit_game
    end
  end


